import React from 'react'
import { Route, Switch, Redirect} from 'react-router-dom'
//import App from './App'
import Header from './Header'
import Login from './Login'
import Register from './Register'
import ProtectedRoute from './ProtectedRoute'
import * as auth from './Auth';


function MegaRouter() {
  const [loggedIn, setLoggedIn] = React.useState(false)
  const [userData, setUserData] = React.useState({
    identifier: '',
    password: ''
  });

  function handleLoginClick(identifier, password) { /* функция при нажатие на кнопку */
    //e.preventDefault()
    console.log('st -_-_- > ', identifier, password)
    auth.authorize(identifier, password)
      .then(obj => {
        console.log('mr -_- > ', obj)
      })
      .catch( err => console.log(' er er -> ', err))
  }

  function handleRegClick(identifier, password) { /* функция при нажатие на кнопку */
    //e.preventDefault()
    console.log('reg -_-_- > ', identifier, password)
    auth.authorize(identifier, password)
      .then(obj => {
        console.log('re -_- > ', obj)
      })
      .catch( err => console.log(' er er -> ', err))
  }

  return (
    <Switch>

    <Route path="/sign-in">
      <div className='page'>
        <Header title='Регистрация' link='/sign-up' />
        <Login onLoginClick={handleLoginClick}/>
      </div>
    </Route>
    <Route path="/sign-up">
      <div className='page'>
        <Header title='Войти' link='/sign-in' />
        <Register onRegClick={handleRegClick}/>
      </div>
    </Route>
    <ProtectedRoute
        path="/"
        loggedIn={loggedIn}
    />
    <Route>
        {loggedIn ? <Redirect to="/" /> : <Redirect to="/sign-in" />}
    </Route>
    </Switch>
  )
}


